const Stack = require('./stack.js');

class StackWithMin extends Stack {
    constructor() {
        super();
        this._mins = [];
    }

    pop() {
        const item = super.pop();

        if (item === this.min()) {
            this._mins.pop();
        }

        return item;
    }

    push(item) {
        if (!this._mins.length || item <= this._mins[this._mins.length - 1]) {
            this._mins.push(item);
        }

        super.push(item);
    }

    min() {
        if (this._mins.length) {
            return this._mins[this._mins.length - 1];
        }

        return null;
    }

}


module.exports = StackWithMin;